    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Planning index{% endblock %}

{% block body %}
    <h1>Planning index</h1>

    <table class=\"table\">
        <thead>
            <tr>
              <th>Nom et prenom</th>
                <th>Date</th>
                <th>Commentaire</th>
                <th>Adresse</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for planning in plannings %}
            <tr>
                <td>
                    {{ planning.idLead ? planning.idLead.nomComplet : 'N/A' }}</td>
                <td>{{ planning.date ? planning.date|date('Y-m-d H:i:s') : '' }}</td>
                <td>{{ planning.commentaire | striptags | replace({'&euro;': '€'})| replace({'&eacute;': 'é'})}}</td>
                <td>{{ planning.adresse }}</td>

                <td>
                    <a href=\"{{ path('app_planning_show', {'id': planning.id}) }}\">show</a>
                    <a href=\"{{ path('app_planning_edit', {'id': planning.id}) }}\">edit</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('app_planning_new') }}\">Create new</a>
{% endblock %}
", "planning/index.html.twig", "/Applications/MAMP/htdocs/CRM/CRM_project_1/templates/planning/index.html.twig");
